import React from "react";


class LessDetailedAtomicElement extends React.Component {
	constructor(props) {
        super(props);
    }

    _renderMoreDetailedVersion() {
        let moreDetailed = document.getElementById("moreDetailed" + this.props.elementName);

    }
	
    render() {
        return (
            <div id={"lessDetailed" + this.props.elementName} className={"bordered " + this.props.type}
                onMouseOver={this._renderMoreDetailedVersion()}>
				<div className="lessDetailed centered">
					{this.props.atomicNumber}
				</div>

                <div className="elementSymbol lessDetailed bolded centered">
					{this.props.symbol}
				</div>
			</div>
		);
	}
}

class MoreDetailedAtomicElement extends React.Component {
	constructor(props) {
        super(props);
	}
	
	render() {	
        return (
            <div id={"moreDetailed" + this.props.elementName} className={"bordered " + this.props.type}>
				<div className="moreDetailed centered">
					{this.props.atomicNumber}et
				</div>
				
                <div className="elementSymbol moreDetailed bolded centered">
					{this.props.symbol}
				</div>
				
				<div className="moreDetailed centered">
					{this.props.elementName}
				</div>
				
				<div className="moreDetailed centered">
					{this.props.atomicWeight}
				</div>
			</div>
		);
	}
}

export {LessDetailedAtomicElement, MoreDetailedAtomicElement}
