import React from "react";


var json = require("./ElementJSON");

class DetailedElement extends React.Component {
    constructor(props) {
        super(props);
    }

    render() {
        /*let html = [];

        for (let element in json.elementJSON.data) {
            let innerHTML = (
                <div ng-switch-when={element} className={"bordered centered detailedElement" + element.type}>
                    <div>
                        {element.atomicNumber}
                    </div>

                    <div>
                        {element.symbol}
                    </div>

                    <div>
                        {element.name}
                    </div>

                    <div>
                        {element.atomicWeight}
                    </div>
                </div>
            );

            html.push(innerHTML);
        }

        html.push(
            <div className="bordered detailedElement" ng-switch-default></div>
        );

        return (
            <div ng-switch="selectedElement">
                {html}
            </div>
        );*/

        let html = "";
        let data = json.elementJSON.data;

        for (let element in data) {
            html += `<div ng-switch-when=${element} class=${"bordered centered detailedElement " + data[element].type}>
                        <div>
                            ${data[element].atomicNumber}
                        </div>

                        <div>
                            ${element.symbol}
                        </div>

                        <div>
                            ${element}
                        </div>

                        <div>
                            ${element.atomicWeight}
                        </div>
                     </div>`
        }

        return (
            <div dangerouslySetInnerHTML={{ __html: html }} />
        );
    }
}

export default DetailedElement
