import React from "react";


class TemperatureSlider extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            value: 0
        };

        this.handleChange = this.handleChange.bind(this);
    }

    handleChange(event) {
        this.setState({value: event.target.value});
    }

    render() {
        return (
            <div>
                <input id="temperatureSlider" className="generalSlider" type="range" min="0" max="6000"
                    value={this.state.value} onChange={this.handleChange} />
                <input id="temperatureText" className="centered valueBox" type="text"
                    value={this.state.value} onChange={this.handleChange} /> 
            </div>
        );
    }
}

class PressureSlider extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            value: 0
        };

        this.handleChange = this.handleChange.bind(this);
    }

    handleChange(event) {
        let currentValue = event.target.value;

        if (currentValue > this.props.max) {
            currentValue = this.props.max;
        }

        this.setState({value: currentValue});
    }

    render() {
        return (
            <div>
                <input id="pressureSlider" className="generalSlider" type="range" min={this.props.min} max={this.props.max}
                    value={this.state.value} onChange={this.handleChange} />
                <input id="pressureText" className="centered valueBox" type="text"
                    value={this.state.value} onChange={this.handleChange} />
            </div>
        );
    }
}

class SliderCollection extends React.Component {
    constructor(props) {
        super(props);
    } 

    render() {
        return (
            <div>
                <TemperatureSlider min="0" max="6000" />
                <PressureSlider min="0" max="6000" />
            </div>
        );
    }
}

export default SliderCollection
