import React from "react";


var elemJSON = require("./ElementJSON").elementJSON;

class TemperatureSlider extends React.Component {
    constructor(props) {
        super(props);
    }

    _updateAtomicElementStates(idSource, idTarget) {
        let temperature = document.getElementById(idSource).value;
        document.getElementById(idTarget).value = temperature;
        this._editStates(temperature);
    }

    _editStates(temperature) {
        for (let elem in elemJSON) {
            let elemDiv = document.getElementById(elem);
            let phaseState = this._computePhaseState(elem, temperature);
            //Edit state color
        }
    } 

    _computePhaseState(elem, temperature) {
        if (elem.boilingPoint !== "unknown") {
            if (temperature > elem.boilingPoint) {
                return "gas";
            }
        }

        if (elem.meltingPoint !== "unknown") {
            if (temperature < elem.meltingPoint) {
                return "solid";
            }
            else {
                return "liquid";
            }
        }

        return "unknown";
    }

    render() {
        return (
            <div>
                <form>
                    <input id="temperatureSlider" className="tempSlider" type="range"
                        min="1" max="6000" value="0"
                        onChange={(event) => this._updateAtomicElementStates("temperatureSlider", "temperatureInput")} />
                    <input id="temperatureInput" className="tempInput" type="text"
                        value="0"
                        onKeyDown={(event) => this._updateAtomicElementStates("temperatureInput", "temperatureSlider")} />
                </form>
            </div>
        );        
    }
}

export default TemperatureSlider
