import React from "react";


var $ = require("jquery");

class NumberRow extends React.Component {
	constructor(props) {
		super(props);
    }

    _highlightColumn(groupNumber) {
        let id = "#group" + groupNumber;

        $(id).removeClass("unhighlight");
        $(id).addClass("highlight");

        this._highlightRelevantElements(index);
    }

    _unhighlightColumn(groupNumber) {
        let id = "#group" + groupNumber;

        $(id).removeClass("highlight");
        $(id).addClass("unhighlight");
    }
	
    render() {
        let html = [];

        for (let index = 1; index <= 18; index++) {
            let value;

            if (index === 1) {
                value = <a id={"group" + index} className="number firstRowNumber bolded centered handPointer textColor unhighlight"
                           onMouseOver={(event) => this._highlightColumn(index)}
                           onMouseOut={(event) => this._unhighlightColumn(index)}
                           onClick={(event) => window.open("https://en.wikipedia.org/wiki/Group_" + index + "_element")}>
                            {index}
                        </a>;
            } 
            else if (index < 10) {
                value = <a id={"group" + index} className="number numberRow bolded centered handPointer textColor unhighlight"
                           onMouseOver={(event) => this._highlightColumn(index)}
                           onMouseOut={(event) => this._unhighlightColumn(index)}
                           onClick={(event) => window.open("https://en.wikipedia.org/wiki/Group_" + index + "_element")}>
                            {index}
                        </a>;
            }
            else {
                value = <a id={"group" + index} className="number doubleDigitNumberRow bolded centered handPointer textColor unhighlight"
                           onMouseOver={(event) => this._highlightColumn(index)}
                           onMouseOut={(event) => this._unhighlightColumn(index)}
                           onClick={(event) => window.open("https://en.wikipedia.org/wiki/Group_" + index + "_element")}>
                            {index}
                        </a>;
            }

            html.push(value);
        }

        return (
            <div id="columnNumbers">
                <div className="rowOfNumbers">
                    {html}
                </div>
            </div>
		);
	}
}

class NumberColumn extends React.Component {
	constructor(props) {
		super(props);
    }

    _highlightRow(periodNumber) {
        let id = "#period" + periodNumber;

        $(id).removeClass("unhighlight");
        $(id).addClass("highlight");
    }

    _unhighlightRow(periodNumber) {
        let id = "#period" + periodNumber;

        $(id).removeClass("highlight");
        $(id).addClass("unhighlight");
    }
	
    render() {
        let html = [];

        for (let index = 1; index <= 7; index++) {
            let value;

            if (index === 1) {
                value = <a id={"period" + index} className="number firstColumnNumber bolded centered handPointer textColor unhighlight"
                           onMouseOver={(event) => this._highlightRow(index)}
                           onMouseOut={(event) => this._unhighlightRow(index)}
                           onClick={(event) => window.open("https://en.wikipedia.org/wiki/Period_" + index + "_element")}>
                            {index}
                        </a>;
            }
            else {
                value = <a id={"period" + index} className="number numberColumn bolded centered handPointer textColor unhighlight"
                           onMouseOver={(event) => this._highlightRow(index)}
                           onMouseOut={(event) => this._unhighlightRow(index)}
                           onClick={(event) => window.open("https://en.wikipedia.org/wiki/Period_" + index + "_element")}>
                            {index}
                        </a>;
            }

            html.push(value);
        }

        return (
            <div id="rowNumbers">
                <div className="columnOfNumbers">
                    {html}
                </div>
            </div>
		);
	}
}

export {NumberRow, NumberColumn}
