import React from "react";


var elemJSON = require("./ElementJSON").elementJSON;

class TemperatureSlider extends React.Component {
    constructor(props) {
        super(props);
    }

    _updateAtomicElementStates(idSource, idTarget) {
        let temperature = document.getElementById(idSource).value;

        if (idSource === "temperatureInput") {
            temperature = this._checkTemperatureValue(temperature);
        }

