import elemJSON from "./ElementJSON";


const chemCalc = {
    _elementData: elemJSON.retrieve().data,

    computePhaseState: function(element, temperature) {
        if (this._elementData[element].boilingPoint !== "unknown") {
            if (this._elementData[element].boilingPoint < temperature) {
                return "gas";
            }
        }

        if (this._elementData[element].meltingPoint !== "unknown") {
            if (this._elementData[element].meltingPoint > temperature) {
                return "solid";
            }
            else {
                return "liquid";
            }
        }

        return "unknown";
    }
};


export default chemCalc
