import React from "react";


class TemperatureSlider extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            value: 0
        };

        this.handleChange = this.handleChange.bind(this);
    }

    handleChange(event) {
        let currentValue = event.target.value.toString();

        if (isNaN(currentValue)) {
            let editedValue = "";

            for (let index = 0; index < currentValue.length; index++) {
                let character = currentValue.charAt(index);

                if (character.match("[1-9]")) {
                    editedValue += character;
                }
            }

            currentValue = editedValue;
        }
        else if (currentValue > this.props.maximum) {
            currentValue = this.props.maximum;
        }

        this.setState({value: currentValue});
    }

    render() {
        return (
            <div>
                <input id="temperatureSlider" className="generalSlider" type="range" min={this.props.minimum} max={this.props.maximum}
                    value={this.state.value} onChange={this.handleChange} />
                <input id="temperatureText" className="centered valueBox" type="text" maxLength="4"
                    value={this.state.value} onChange={this.handleChange} /> 
            </div>
        );
    }
}

class PressureSlider extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            value: 0
        };

        this.handleChange = this.handleChange.bind(this);
    }

    handleChange(event) {
        let currentValue = event.target.value.toString();

        if (isNaN(currentValue)) {
            let editedValue = "";

            for (let index = 0; index < currentValue.length; index++) {
                let character = currentValue.charAt(index);

                if (character.match("[1-9]")) {
                    editedValue += character;
                }
            }

            currentValue = editedValue;
        }
        else if (currentValue > this.props.maximum) {
            currentValue = this.props.maximum;
        }

        this.setState({value: currentValue});
    }

    render() {
        return (
            <div>
                <input id="pressureSlider" className="generalSlider" type="range" min={this.props.minimum} max={this.props.maximum}
                    value={this.state.value} onChange={this.handleChange} />
                <input id="pressureText" className="centered valueBox" type="text" maxLength="4"
                    value={this.state.value} onChange={this.handleChange} />
            </div>
        );
    }
}

class SliderCollection extends React.Component {
    constructor(props) {
        super(props);
    } 

    render() {
        return (
            <div>
                <TemperatureSlider minimum="0" maximum="6000" />
                <PressureSlider minimum="0" maximum="6000" />
            </div>
        );
    }
}

export default SliderCollection
