import React from "react";


function calculateNewPhase(boilingPoint, meltingPoint) {
    let tempValue = document.getElementById("tempSlider").value;

    if (tempValue >= boilingPoint) {
        return "gasColor";
    }
    else if (tempValue <= meltingPoint) {
        return "solidColor";
    }
    else {
        return "liquidColor";
    }
}


class Slider extends React.Component {
    constructor(props) {
        super(props);

        this.state = {value: 0};
        this.handleChange = this.handleChange.bind(this);
    }

    handleChange(event) {
        let currentValue = event.target.value;

        if (isNaN(currentValue)) {
            currentValue = this.editOutAlphabetChars(currentValue);
        }
        else if (Number(currentValue) > this.props.maximum) {
            currentValue = this.props.maximum;
        } 

        this.setState({value: currentValue});
    }

    editOutAlphabetChars(currentValue) {
        let editedValue = "";

        for (let index = 0; index < currentValue.length; index++) {
            let character = currentValue.charAt(index);

            if (character.match("[1-9]")) {
                editedValue += character;
            }
        }

        return editedValue;
    }

    render() {
        return (
            <div>
                <input id={this.props.id + "Slider"} className="generalSlider" type="range" min={this.props.minimum} max={this.props.maximum}
                    value={this.state.value} onChange={this.handleChange} />
                <input id={this.props.id + "Slider"} className="centered valueBox" type="text" maxLength="4"
                    value={this.state.value} onChange={this.handleChange} /> 
            </div>
        );
    }
}

class SliderCollection extends React.Component {
    constructor(props) {
        super(props);
    }

    handleChange(_) {
        let elementSymbols = document.getElementsByClassName("elementSymbol");
        let newPhase = calculateNewPhase(this.props.boilingPoint, this.props.meltingPoint);

        for (let index = 0; index < elementSymbols.length; index++) {
            let elem = elementSymbols[index];

            elem.classList.remove("solidColor");
            elem.classList.remove("liquidColor");
            elem.classList.remove("gasColor");
            elem.classList.remove("unknownColor");

            elem.classList.add(newPhase);
        }
    }

    render() {
        return (
            <div onChange={this.handleChange}>
                <label htmlFor="temp">Temperature (Kelvin)</label>
                <Slider id="temp" minimum="0" maximum="6000" />
            </div>
        );
    }
}

export default SliderCollection
